import './PersonalInfo.css'; // Ensure you have created this CSS file.
import React, { useState, useEffect } from 'react';


function PersonalInfo() {



// Assuming you have other state variables
// useState for username, to ensure user is logged ibn
// user signin status
const [username] = useState('temp_user');

// useState to handle files
const [profilePic, setProfilePic] = useState(null);

// useState to GET/POST data from backend
const [data, setProfileData] = useState({
  profile_picture: '',
  first_name: '',
  last_name: '',
  date_of_birth: '', // Assuming 'YYYY-MM-DD' format
  email: '',
  contact: '',
  location: ''
});

useEffect(() => {
  const fetchData = async () => {
    if (username) {
      try {
        const response = await fetch(`http://localhost:8000/api/personalinfo/${username}/`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        console.log(data)
        setProfileData({
          profile_picture: data.profile_picture || '',
          first_name: data.first_name || '',
          last_name: data.last_name || '',
          date_of_birth: data.date_of_birth || '',
          email: data.email || '',
          contact: data.contact || '',
          location: data.location || '',
          // Ensure you're covering all fields and providing fallbacks
        });

      } catch (error) {
        console.error("An error occurred while fetching the profile data:", error);
      }
    }
  };

  fetchData();
}, [username]); // Dependency on username


const handleProfilePicChange = (event) => {
  // This is to display the selected image on the frontend
  const file = event.target.files[0];
  if (file) {
    setProfilePic(URL.createObjectURL(file));
  }
};

const handleProfilePicUpload = async () => {
  const formData = new FormData();
  const fileField = document.querySelector('input[type="file"]');

  formData.append('file', fileField.files[0]);

  try {
    const response = await fetch(`http://localhost:8000/api/upload_profile_picture/${username}/`, {
      method: 'PUT',
      body: formData
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    // Handle successful response
  } catch (error) {
    console.error("Error uploading profile picture:", error);
    // Handle errors here
  }
};

const handleFormSubmit = async (e) => {
  e.preventDefault(); // Prevent the default form submission

  try {
    // Assuming you want to update the personal info
    const response = await fetch(`http://localhost:8000/api/personalinfo/${username}/`, {
      method: 'PUT', // Use PUT for update operations
      headers: {
        'Content-Type': 'application/json',
        // Include authentication headers if needed
      },
      body: JSON.stringify(data),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    console.log("Profile updated successfully.");
    // Additional logic upon successful update
  } catch (error) {
    console.error("Error updating profile:", error);
  }
};

  return (
    <div className="personal-info">
      <h1>Personal Information</h1>
      <form onSubmit={handleFormSubmit}>
      <div className="form-group profile-pic">
          <label htmlFor="profilePicUpload">Profile Picture</label>
          {profilePic && <img src={profilePic} alt="Profile" className="profile-preview" />}
          <input type="file" id="profilePicUpload" accept="image/*" onChange={handleProfilePicChange} />
          <button onClick={handleProfilePicUpload}>Upload </button>
        </div>
        <div className="form-group">
          <label htmlFor="firstName">First Name</label>

          <input type="text" id="firstName" name="firstName" value = {data.first_name} 
          onChange={e => setProfileData({ data, first_name: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="lastName">Last Name</label>
          <input type="text" id="lastName" name="lastName" 
          value = {data.last_name} 
          onChange={e => setProfileData({ data, last_name: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="dateOfBirth">Date of Birth</label>
          <input type="date" id="dateOfBirth" name="dateOfBirth" 
          value = {data.date_of_birth} 
          onChange={e => setProfileData({ data, date_of_birth: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input type="text" id="location" name="location" 
          value = {data.location} 
          onChange={e => setProfileData({ data, location: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" 
          value = {data.email} 
          onChange={e => setProfileData({ data, email: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="contact">Contact Number</label>
          <input type="tel" id="contact" name="contact"
          value = {data.contact} 
          onChange={e => setProfileData({ data, contact: e.target.value })}
          />
        </div>

        <div className="form-actions">
          <button type="submit">Save Changes</button>
        </div>

      </form>
    </div>
  );
}

export default PersonalInfo;
