import './PersonalInfo.css'; // Ensure you have created this CSS file.
import React, { useState } from 'react';

function PersonalInfo() {
  const [profilePic, setProfilePic] = useState(null);

  // ... handleProfilePicChange remains the same


  // Handle file selection
  const handleProfilePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // For demonstration, we'll just use the local object URL to show the image
      setProfilePic(URL.createObjectURL(file));
    }
  };


  return (
    <div className="personal-info-container">
      <div className="left-panel">
        <h1>General Information</h1>
        <form>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="firstName">First Name</label>
              <input type="text" id="firstName" name="firstName" placeholder="Enter your first name" required />
            </div>
            <div className="form-group">
              <label htmlFor="lastName">Last Name</label>
              <input type="text" id="lastName" name="lastName" placeholder="Also your last name" required />
            </div>
          </div>
          {/* Continue structuring your form groups into rows as needed */}
          {/* ... Additional form fields ... */}
          <div className="form-actions">
            <button type="submit">Save All</button>
          </div>
        </form>
      </div>
      <div className="right-panel">
        <div className="profile-summary">
          <img src={profilePic || "default-profile-pic.png"} alt="Profile" className="profile-preview" />
          {/* Static name and title for now, dynamic data can be loaded */}
          <h2>Neil Sims</h2>
          <p>Senior Software Engineer</p>
          <p>New York, USA</p>
          <button>Connect</button>
          <button>Send Message</button>
        </div>
        <div className="profile-photo-update">
          <label htmlFor="profilePicUpload">Select profile photo</label>
          <input type="file" id="profilePicUpload" accept="image/*" onChange={handleProfilePicChange} />
          <p className="file-instructions">JPG, GIF or PNG. Max size of 800K</p>
        </div>
      </div>
    </div>
  );
}

export default PersonalInfo;
